import React, { useState } from 'react';
import './App.css';

function App() {
  // State to store the list of tasks
  const [tasks, setTasks] = useState([]);
  // State to store the current input field value
  const [task, setTask] = useState('');

  // Function to add a task
  const addTask = () => {
    if (!task) return; // Prevent adding empty tasks
    setTasks([...tasks, task]); // Add the task to the list
    setTask(''); // Clear the input field
  };

  // Function to delete a task
  const deleteTask = (index) => {
    const newTasks = tasks.filter((_, i) => i !== index); // Remove task by index
    setTasks(newTasks); // Update the tasks list
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>My To-Do App</h1>
        <input
          value={task}
          onChange={(e) => setTask(e.target.value)}
          type="text"
          placeholder="Add a new task"
        />
        <button onClick={addTask}>Add Task</button>
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>
              {task} <button onClick={() => deleteTask(index)}>Delete</button>
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
