// Import React and useState hook from React library
import React, { useState } from 'react';
// Import the stylesheet for styling the component
import './App.css';

// Define the App component as a functional component
function App() {
  // useState hook to keep track of tasks array (list of tasks)
  const [tasks, setTasks] = useState([]);
  // useState hook to keep track of the current input value for a new task
  const [task, setTask] = useState('');

  // Function to add a new task to the tasks array
  const addTask = () => {
    if (!task) return; // Prevent adding empty tasks
    setTasks([...tasks, task]); // Spread existing tasks and add new task to the array
    setTask(''); // Reset input field to empty after adding a task
  };

  // Function to delete a task based on its index
  const deleteTask = (index) => {
    // Filter out the task that has the index and update the tasks array
    setTasks(tasks.filter((_, i) => i !== index));
  };

  // Render the component to the DOM
  return (
    <div className="App">
      <header className="App-header">
        <h1>To-Do List</h1>
        <div>
          {/* Text input for entering new tasks */}
          <input
            value={task} // Controlled input using React state
            onChange={(e) => setTask(e.target.value)} // Update state upon input change
            type="text"
            placeholder="Enter a new task" // Placeholder text for input
          />
          {/* Button to add a new task */}
          <button onClick={addTask}>Add</button>
        </div>
        {/* List of tasks */}
        <ul>
          {tasks.map((task, index) => (
            // Map each task to a list item
            <li key={index}> {/* Unique key for each child in list */}
              {task} {/* Display the task text */}
              {/* Button to delete a task, uses the deleteTask function with current index */}
              <button className="delete" onClick={() => deleteTask(index)}>Delete</button>
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

// Export the App component so it can be used in other parts of the app
export default App;
