import React, { useState } from 'react';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import backgroundImage from './assets/glenn-carstens-peters-RLw-UC03Gwc-unsplash.jpg'; // Import your background image here


function App() {
  const [tasks, setTasks] = useState([]);
  const [task, setTask] = useState('');

  const addTask = () => {
    if (!task) return;
    setTasks([...tasks, task]);
    setTask('');
  };

  const deleteTask = (index) => {
    setTasks(tasks.filter((_, i) => i !== index));
  };

  return (
    <div className="App" style={{
      backgroundImage: `url(${backgroundImage})`, // Apply background image here
      backgroundSize: 'cover', // Cover the entire area of the div
      backgroundPosition: 'center', // Center the background image
      minHeight: '100vh' // Ensure it covers at least the entire height of the viewport
    }}>
      <header className="App-header">
        <h1>To-Do List</h1>
        <div>
          <input
            value={task}
            onChange={(e) => setTask(e.target.value)}
            type="text"
            placeholder="Enter a new task"
          />
          <button onClick={addTask}>Add</button>
        </div>
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>
              {task}
              <button className="delete" onClick={() => deleteTask(index)}>
                <FontAwesomeIcon icon={faTrash} /> {/* Icon replacing text */}
              </button>
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
