import React, { useState } from 'react';
import './App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg|icons';

function App() {
  // State to keep track of all tasks and their editing statuses
  const [tasks, setTasks] = useState([]);

  // State to keep track of the new task input
  const [task, setTask] = useState('');

  // Functions to manipulate the state will go here
  const addTask = () => {
    if (!task.trim()) return;
    setTasks([...tasks, { id: Date.now(), text: task, isEditing: false }]);
    setActions(''); // Clear the input after adding a task
  };

  // Further functions for editing and deleting tasks will also go here

  return (
    <div className="App">
      <header className="App-header">
        <h1>To-Do List</h1>
        <div>
          <input
            value={task}
            onChange={(e) => setTask(e.target.value)}
            type="text"
            placeholder="Enter a new task"
          />
          <button onClick={addTask}>Add</button>
        </div>
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>
              {task.isEditing ? (
                <input
                  type="text"
                  value={task.text}
                  onChange={(e) => editTask(task.id, e.target.value)}
                  onBlur={() => stopEditing(task.id)}
                  autoFocus
                />
              ) : (
                <>
                  {task.text}
                  <button onClick={() => startEditing(task.id)}>Edit</button>
                </>
              )}
              <button className="delete" onClick={() => deleteTask(task.id)}>
                <FontAwesomeIcon icon={faTrash} />
              </button>
            </li>
          ))}
        </ul>
      </header>
    </div>
  );
}

export default App;
